'use strict';

var promisify = require('promisify-es6');

module.exports = function (send) {
  return {
    gen: promisify(function (args, opts, callback) {
      if (typeof opts === 'function') {
        callback = opts;
        opts = {};
      }
      send({
        path: 'key/gen',
        args: args,
        qs: opts
      }, callback);
    }),
    list: promisify(function (opts, callback) {
      if (typeof opts === 'function') {
        callback = opts;
        opts = {};
      }
      send({
        path: 'key/list',
        qs: opts
      }, callback);
    })
  };
};