'use strict';

var baseTable = require('./base-table');
var varintBufferEncode = require('./util').varintBufferEncode;

// this creates a map for codecName -> codeVarintBuffer

var varintTable = {};
module.exports = varintTable;

for (var encodingName in baseTable) {
  var code = baseTable[encodingName];
  varintTable[encodingName] = varintBufferEncode(code);
}